import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [showForm, setShowForm] = useState(false);
  const [showLinks, setShowLinks] = useState(false);
  const [formData, setFormData] = useState({ name: '', email: '' });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/send-resume-request', formData);
      setShowLinks(true);
    } catch (error) {
      alert('Error sending email');
    }
  };

  return (
    <div className="app">
      <button className="resume-button" onClick={() => setShowForm(true)}>Resume</button>

      {showForm && (
        <div className="modal">
          <div className="modal-content">
            <h3>Enter Details to Access Resume</h3>
            {!showLinks ? (
              <form onSubmit={handleSubmit}>
                <input
                  type="text"
                  placeholder="Your Name"
                  required
                  value={formData.name}
                  onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                />
                <input
                  type="email"
                  placeholder="Your Email"
                  required
                  value={formData.email}
                  onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                />
                <button onClick={() => setShowForm(false)}>Cancel</button>
              </form>
            ) : (
              <div>
                <p>Thanks! You can now access the resume below:</p>
                <a href="/resume.pdf" target="_blank" rel="noreferrer">View Resume</a><br />
                <a href="/resume.pdf" download>Download Resume</a>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
